DROP TABLE IF EXISTS usuario_pelicula;
CREATE TABLE usuario_pelicula (
  usuario bigint NOT NULL,
  pelicula bigint NOT NULL,
  PRIMARY KEY (usuario,pelicula),
  KEY usuario (usuario),
  KEY pelicula (pelicula),
  CONSTRAINT id_usuario FOREIGN KEY (usuario) REFERENCES usuario (id) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT id_pelicula FOREIGN KEY (pelicula) REFERENCES pelicula (id) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci COMMENT='Tabla de favoritos';

